import React, { useState } from 'react';
import { NavLink } from "react-router-dom";
import { Registrar } from "./Registrar";
import { Listar } from "./Listar";
import { Estadistica } from "./Estadistica";
import { DatePicker } from 'antd';
import {
  DesktopOutlined,
  FileOutlined,
  PieChartOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
const { Header, Content, Footer, Sider } = Layout;

const getItem = (label, key, icon, children) => {
  return {
    key,
    icon,
    children,
    label,
  };
};

const items = [
  getItem('Dashboard', '1', <PieChartOutlined />),
  getItem('List of Prices', '2', <DesktopOutlined />),
  getItem('Clients', 'sub1', <UserOutlined />, [
    getItem('Phone', '3'),
    getItem('PC', '4'),
    getItem('Others', '5'),
  ]),
  getItem('Logout', '9', <FileOutlined />, () => cerrarSesion()),
];

const cerrarSesion = () => {
  // Implement logout logic here
};

const App = () => {
  const [collapsed, setCollapsed] = useState(false);
  const [activeComponent, setActiveComponent] = useState('');
  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  const handleMenuClick = (key) => {
    switch (key) {
      case '1':
        setActiveComponent('registrar');
        break;
      case '2':
        setActiveComponent('listar');
        break;
      case '3':
        setActiveComponent('estadistica');
        break;
      default:
        setActiveComponent('');
    }
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)}>
        <div className="demo-logo-vertical" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items} onClick={(item) => handleMenuClick(item.key)} />
      </Sider>
      <Layout>
        <Header style={{ padding: 0, background: colorBgContainer }} />
        <Content style={{ margin: '0 16px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>User</Breadcrumb.Item>
            <Breadcrumb.Item>Bill</Breadcrumb.Item>
          </Breadcrumb>
          <div style={{ padding: 24, minHeight: 360, background: colorBgContainer, borderRadius: borderRadiusLG }}>
            Bill is a cat.
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          Ant Design Â©{new Date().getFullYear()} Created by Ant UED
        </Footer>
      </Layout>
      {activeComponent === 'registrar' && <Registrar />}
      {activeComponent === 'listar' && <Listar />}
      {activeComponent === 'estadistica' && <Estadistica />}
    </Layout>
  );
};

export default App;
