import React, { useState } from 'react'
import { Menu } from './Menu'

export const Login = () => {

  const [miLogin, setMiLogin] = useState("false");
  const [usu, setUsu] = useState("");
  const [pas, setPas] = useState("");


  function iniciarSesion(e){
    e.preventDefault();
    var txtusu = document.getElementById("txtusu").value;
    var txtpas = document.getElementById("txtpas").value;
    if(txtusu.length===0 || txtpas.length===0){
      alert("Complete Los Datos Faltantes!!");
    }else{
      if(usu === "admin" && pas==="123"){
        setMiLogin("true");
        document.getElementById("form_login").style.display = "none";
      }else{
        setMiLogin("false");
        alert("Error De Usuario y/o Contraseña!!");
        document.getElementById("txtusu").value = "";
        document.getElementById("txtpas").value = "";
        document.getElementById("txtusu").focus();
        
      }
    }
  }


  return (
    

    <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8" >
        <div className=' mt-10  sm:w-full sm:max-w-sm'>
    <form id="form_login" className='space-y-6'>
        <div>
           
        <div className='flex items-center justify-between'>
            <label htmlFor="txtusu" className="block text-sm font-medium leading-6 text-gray-900" ><strong>Usuario</strong></label>
            </div>   
            <div className="mt-2"  >
            <input type="text" id="txtusu"  className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"  onChange={ (e)=>setUsu(e.target.value) }  required/>
            </div>


        </div>



        <div>
             
              
            </div>
        <div>
          <div className='flex items-center justify-between'>
                 <label className='block text-sm font-medium leading-6 text-gray-900' htmlFor="txtpas"><strong>Contraseña</strong>
                 </label>
            </div>
            <div className='mt-2'>

            <input type="password" id="txtpas" style={{textAlign:"center"}} className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"  onChange={ (e)=>setPas(e.target.value) }  required/>

            </div>
            
        </div><br/>
       
        
        <input type="submit"  className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600" value="iniciar seccion" onClick={ iniciarSesion }/>
       
    </form>

    { miLogin === "true" && <Menu usu={usu}/> }
</div>
</div>


  )
}
